import React from "react";

const skills = [
  { icon: "/images/html.webp", name: "HTML" },
  { icon: "/images/css.webp", name: "CSS" },
  { icon: "/images/js.webp", name: "JavaScript" },
  { icon: "/images/react.webp", name: "React" },
  { icon: "/images/php.webp", name: "PHP" },
  { icon: "/images/python.webp", name: "Python" },
  { icon: "/images/django.webp", name: "Django" },
  { icon: "/images/git.webp", name: "Git" },
  { icon: "/images/scrum.webp", name: "Scrum" },
  { icon: "/images/wordpress.webp", name: "WordPress" },
  { icon: "/images/tailwind.webp", name: "Tailwind" },
  { icon: "/images/bootstrap.webp", name: "Bootstrap" },
];

const Skills = () => {
  return (
    <section id="my-skills" className="bg-color5 text-white py-10 px-8">
      <h2 className="text-3xl font-bold text-center mb-6">Habilidades</h2>
      <div className="relative p-[4px] rounded-lg">
        {/* Contenedor con borde animado */}
        <div className="absolute inset-0 bg-gradient-to-r from-pink-500 via-purple-500 to-blue-500 animate-border-spin rounded-lg"></div>

        {/* Contenedor principal con los Ã­conos */}
        <div className="relative bg-color5 p-6 rounded-lg grid grid-cols-3 sm:grid-cols-4 lg:grid-cols-6 gap-3 place-items-center">
          {skills.map((skill, index) => (
            <div key={index} className="aspect-square flex flex-col items-center gap-1">
              <img src={skill.icon} alt={skill.name} className="w-20 h-full object-contain" />
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Skills;
