import React, { useState, useEffect } from "react";
import { Menu, X } from "lucide-react"; // Íconos de menú

const Header = () => {
  const [scrolled, setScrolled] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <header
      className={`fixed top-0 left-0 w-full p-4 transition-all duration-300 ease-in-out z-50 
      ${scrolled ? "bg-black/40 backdrop-blur-md shadow-lg" : "bg-transparent"}`}
    >
      <div className="container mx-auto flex justify-between items-center">
        {/* Logo */}
        <h1 className="text-white text-lg font-bold">Mi Portafolio</h1>

        {/* Menú para desktop */}
        <nav className="hidden md:flex space-x-6">
          <a href="#home" className="text-white hover:text-gray-300">Inicio</a>
          <a href="#about" className="text-white hover:text-gray-300">Sobre mí</a>
          <a href="#projects" className="text-white hover:text-gray-300">Proyectos</a>
          <a href="#contact" className="text-white hover:text-gray-300">Contacto</a>
        </nav>

        {/* Botón de menú en móviles */}
        <button
          className="md:hidden text-white"
          onClick={() => setMenuOpen(!menuOpen)}
        >
          {menuOpen ? <X size={28} /> : <Menu size={28} />}
        </button>
      </div>

      {/* Menú desplegable en móviles */}
      {menuOpen && (
        <nav className="md:hidden absolute top-16 left-0 w-full bg-black/80 backdrop-blur-md p-4 flex flex-col items-center space-y-4">
          <a href="#home" className="text-white hover:text-gray-300">Inicio</a>
          <a href="#about" className="text-white hover:text-gray-300">Sobre mí</a>
          <a href="#projects" className="text-white hover:text-gray-300">Proyectos</a>
          <a href="#contact" className="text-white hover:text-gray-300">Contacto</a>
        </nav>
      )}
    </header>
  );
};

export default Header;
