import React from "react";

const skills = [
  { icon: "/images/html.webp", name: "HTML" },
  { icon: "/images/css.webp", name: "CSS" },
  { icon: "/images/js.webp", name: "JavaScript" },
  { icon: "/images/react.webp", name: "React" },
  { icon: "/images/php.webp", name: "PHP" },
  { icon: "/images/python.webp", name: "Python" },
  { icon: "/images/django.webp", name: "Django" },
  { icon: "/images/git.webp", name: "Git" },
  { icon: "/images/scrum.webp", name: "Scrum" },
  { icon: "/images/wordpress.webp", name: "WordPress" },
  { icon: "/images/tailwind.webp", name: "Tailwind" },
  { icon: "/images/bootstrap.webp", name: "Bootstrap" },
];

const Skills = () => {
  return (
    <section id="my-skills" className="relative bg-color5 text-white py-16 px-8">
  {/* Borde animado */}
  <div className="absolute inset-0 -z-10 rounded-lg p-[3px] bg-gradient-to-r from-green-400 via-blue-500 to-purple-600 animate-borderAnimation">
    <div className="h-full w-full bg-color5 rounded-lg"></div>
  </div>

  <h2 className="text-4xl font-bold text-center mb-8">My Skills</h2>
  <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4 justify-items-center p-4">
    {skills.map((skill, index) => (
      <div key={index} className="flex flex-col items-center gap-2 hover:scale-110 transition-transform">
        <img src={skill.icon} alt={skill.name} className="w-16 h-16 object-contain" />
      </div>
    ))}
  </div>
</section>
  );
};

export default Skills;
