import React from "react";
import { Swiper, SwiperSlide } from "swiper/react";
import { Navigation, Pagination, Autoplay } from "swiper/modules";
import "swiper/css";
import "swiper/css/navigation";
import "swiper/css/pagination";
import { TAGS } from "./Tags";

const skills = [
  TAGS.HTML,
  TAGS.JS,
  TAGS.CSS,
  TAGS.GIT,
  TAGS.REACT,
  TAGS.PHP,
  TAGS.WORDPRESS,
  TAGS.PYTHON,
  TAGS.DJANGO,
  TAGS.TAILWIND,
  TAGS.SCRUM,
  TAGS.BOOTSTRAP,
];

const Skills = () => {
  return (
    <section id="my-skills" className="py-10 px-8">
      <h2 className="text-3xl font-bold text-center mb-6 text-white">Habilidades</h2>
      <div className="relative p-4 bg-color5 rounded-3xl shadow-lg">
        {/* Swiper Slider */}
        <Swiper
          modules={[Navigation, Pagination, Autoplay]}
          spaceBetween={20}
          slidesPerView={2}
          breakpoints={{
            640: { slidesPerView: 3 },
            768: { slidesPerView: 4 },
            1024: { slidesPerView: 6 },
          }}
          navigation
          pagination={{ clickable: true }}
          autoplay={{ delay: 2500, disableOnInteraction: false }}
          className="w-full"
        >
          {skills.map((skill, index) => (
            <SwiperSlide key={index} className="flex flex-col items-center gap-2">
              <div className={`p-3 rounded-full ${skill.class}`}>{skill.icon}</div>
              <span className="text-sm font-onest font-medium text-white">{skill.name}</span>
            </SwiperSlide>
          ))}
        </Swiper>
      </div>
    </section>
  );
};

export default Skills;
