import React, { ReactNode } from "react";

interface BackgroundProps {
  children: ReactNode;
}

const Background: React.FC<BackgroundProps> = ({ children }) => {
  return (
    <div className="min-h-screen">
      {/* Sección superior con gradiente animado */}
      <div className="h-[50vh] bg-custom-gradient"></div>

      {/* Sección inferior con color fijo */}
      <div className="bg-fixed-color">{children}</div>
    </div>
  );
};

export default Background;
