import React, { useState, useEffect } from "react";

const Header = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      setScrolled(window.scrollY > 50); // Activa el fondo cuando se baja 50px
    };

    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <header
      className={`fixed top-0 left-1/2 transform -translate-x-1/2 w-1/2 transition-all duration-500 ${
        scrolled
          ? "bg-black/50 backdrop-blur-lg shadow-lg py-3" // Fondo oscuro con sombra y difuminado
          : "bg-transparent py-5"
      } z-50 rounded-b-2xl`}
    >
      <div className="container mx-auto flex justify-center">
        <nav>
          <ul className="flex space-x-6 md:flex-row flex-col md:space-x-6 space-y-4 md:space-y-0">
            <li><a href="#home" className="text-white hover:text-gray-300">Inicio</a></li>
            <li><a href="#about" className="text-white hover:text-gray-300">Sobre m√≠</a></li>
            <li><a href="#projects" className="text-white hover:text-gray-300">Proyectos</a></li>
            <li><a href="#contact" className="text-white hover:text-gray-300">Contacto</a></li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
