import React, { useEffect, useState } from 'react';

const Header: React.FC = () => {
  const [isScrolled, setIsScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 100) {
        setIsScrolled(true);
      } else {
        setIsScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <header
      className={`bg-color2 p-4 m-4 rounded-3xl transition-all duration-300 ${
        isScrolled ? 'fixed top-0 left-1/2 -translate-x-1/2 w-3/4' : 'static w-auto'
      }`}
    >
      <div className="container mx-auto flex justify-between items-center">
        <nav>
          <ul className="flex space-x-6 md:flex-row flex-col md:space-x-6 space-y-4 md:space-y-0">
            <li>
              <a href="#home" className="text-white hover:text-gray-300">
                Inicio
              </a>
            </li>
            <li>
              <a href="#about" className="text-white hover:text-gray-300">
                Sobre m√≠
              </a>
            </li>
            <li>
              <a href="#projects" className="text-white hover:text-gray-300">
                Proyectos
              </a>
            </li>
            <li>
              <a href="#contact" className="text-white hover:text-gray-300">
                Contacto
              </a>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
