import React, { useState } from "react";

const projects = [
  {
    id: 1,
    title: "Portfolio Personal",
    category: "Vanilla",
    description: "Un portfolio minimalista hecho con HTML, CSS y JavaScript.",
    image: "/images/portfolio.png",
    tech: ["HTML", "CSS", "JavaScript"],
    links: {
      demo: "https://example.com/demo-portfolio",
      code: "https://github.com/username/portfolio",
    },
  },
  {
    id: 2,
    title: "E-commerce React",
    category: "Otros",
    description: "Tienda online con React, Tailwind y Firebase.",
    image: "/images/ecommerce.png",
    tech: ["React", "Tailwind", "Firebase"],
    links: {
      demo: "https://example.com/demo-ecommerce",
      code: "https://github.com/username/ecommerce",
    },
  },
  {
    id: 3,
    title: "Blog WordPress",
    category: "WordPress",
    description: "Un blog profesional optimizado con WordPress.",
    image: "/images/blog.png",
    tech: ["WordPress", "PHP"],
    links: {
      demo: "https://example.com/demo-blog",
    },
  },
  {
    id: 4,
    title: "CRM Python",
    category: "Otros",
    description: "Sistema CRM para pequeñas empresas usando Django.",
    image: "/images/crm.png",
    tech: ["Python", "Django"],
    links: {
      code: "https://github.com/username/crm",
    },
  },
];

const Projects = () => {
  const [filter, setFilter] = useState("Todos");

  const filteredProjects =
    filter === "Todos" ? projects : projects.filter((p) => p.category === filter);

  return (
    <section id="projects" className="py-16 px-8 bg-color5 text-white">
      <h2 className="text-4xl font-bold text-center mb-8">Proyectos</h2>
      <div className="flex justify-center gap-4 mb-8">
        {["Todos", "Vanilla", "WordPress", "Otros"].map((category) => (
          <button
            key={category}
            onClick={() => setFilter(category)}
            className={`px-4 py-2 rounded-lg font-medium ${
              filter === category
                ? "bg-color2 text-white"
                : "bg-color3 text-gray-200 hover:bg-color2"
            } transition`}
          >
            {category}
          </button>
        ))}
      </div>
      <div className="grid grid-cols-1 sm:grid-cols-2 gap-6">
        {filteredProjects.map((project) => (
          <div
            key={project.id}
            className="bg-color4 rounded-3xl overflow-hidden shadow-lg hover:scale-105 transition-transform flex flex-col"
          >
            <img
              src={project.image}
              alt={project.title}
              className="w-full h-40 object-cover"
            />
            <div className="p-4 flex-1 flex flex-col">
              <h3 className="text-2xl font-bold mb-2">{project.title}</h3>
              <p className="text-sm opacity-75 mb-4">{project.description}</p>
              <div className="flex flex-wrap gap-2 mb-4">
                {project.tech.map((tech, index) => (
                  <span
                    key={index}
                    className="px-2 py-1 text-xs bg-color3 rounded-3xl"
                  >
                    {tech}
                  </span>
                ))}
              </div>
              <div className="mt-auto flex gap-4">
                {project.links.demo && (
                  <a
                    href={project.links.demo}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-color1 text-white px-4 py-2 rounded-3xl text-sm hover:bg-color2 transition"
                  >
                    Demo
                  </a>
                )}
                {project.links.code && (
                  <a
                    href={project.links.code}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="bg-color3 text-gray-200 px-4 py-2 rounded-3xl text-sm hover:bg-color2 transition"
                  >
                    Código
                  </a>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};

export default Projects;
